class Solution {
public:
    int lengthOfLongestSubstring(string s) {
        int n=s.size();
        unordered_map<char,int> hashtable;
        int left=0,right=0;
        int ans=0;
        while(right<n)
        {
            if(hashtable[s[right]]==0)
            {
                ans=max(ans,right-left+1);
            }
            else
            {
                while(left<=right)
                {
                    if(s[left]==s[right])
                    {
                        --hashtable[s[left]];
                        ++left;
                        break;
                    }
                    else
                    {
                        if(hashtable[s[left]]==1)
                        {
                            --hashtable[s[left]];
                            ++left;
                        }
                        else ++left;
                    }
                }
            }
            ++hashtable[s[right]];
            ++right;
        }
        return ans;
    }
};
```

