class Solution {
public:
    int canCompleteCircuit(vector<int>& gas, vector<int>& cost) {
        int length=gas.size();
        int curOil=0,sumOil=0;
        int start=0;
        for(int i=0;i<length;++i)
        {
            curOil+=gas[i]-cost[i];
            sumOil+=gas[i]-cost[i];
            if(curOil<0)
            {
                start=i+1;
                curOil=0;
            }
        }
        return sumOil<0?-1:start;
    }
};
```

