class Solution {
public:
    bool canPartition(vector<int>& nums) {
        int n=nums.size();
        if(n==1)return false;//åªæœ‰ä¸€ä¸ªæ•°,æ˜¾ç„¶ä¸èƒ½åˆ†å‰²
        int sum=0;
