class Solution {
public:
    int jump(vector<int>& nums) {
        int n=nums.size();
        int maxPos=0,end=0,step=0;
        for(int i=0;i<n-1;i++)
        {
            maxPos=max(maxPos,nums[i]+i);
            if(i==end)
            {
                end=maxPos;
                ++step;
            }
        }
        return step;
    }
};
```

