/**
 * Definition for a binary tree node.
 * public class TreeNode {
 *     int val;
 *     TreeNode left;
 *     TreeNode right;
 *     TreeNode() {}
 *     TreeNode(int val) { this.val = val; }
 *     TreeNode(int val, TreeNode left, TreeNode right) {
 *         this.val = val;
 *         this.left = left;
 *         this.right = right;
 *     }
 * }
 */
class Solution {
    public TreeNode replaceValueInTree(TreeNode root) {
        Queue<TreeNode> tree=new ArrayDeque<>();
        if(root!=null)tree.add(root);
        root.val=0;
        while(!tree.isEmpty())
        {
            int nextLevelSum=0;
            Queue<TreeNode> tmp=tree;
            tree=new ArrayDeque<>();//ç”¨äºåŠ å…¥ä¸‹ä¸€å±‚çš„æ‰€æœ‰ç»“ç‚¹
//å¦‚æœæ‚¨å°è¯•ä¸€è¾¹å¼¹å‡ºèŠ‚ç‚¹ä¸€è¾¹æ·»åŠ ä¸‹ä¸€å±‚çº§çš„èŠ‚ç‚¹ï¼Œé‚£ä¹ˆå½“å‰å±‚çº§çš„èŠ‚ç‚¹ä¼šä¸ä¸‹ä¸€å±‚çº§çš„èŠ‚ç‚¹æ··åˆåœ¨åŒä¸€ä¸ªé˜Ÿåˆ—ä¸­ï¼Œè¿™å°†ä½¿å¾—å¾ˆéš¾ç¡®å®šå“ªäº›èŠ‚ç‚¹å±äºå½“å‰å±‚çº§ï¼Œä»è€Œå¯¼è‡´é”™è¯¯çš„ä¿®æ”¹ã€‚(æ¥æº:ChatGpt)
            for(TreeNode node : tmp)//æ±‚å‡ºæ•´å±‚çš„val
            {
                if(node.left!=null)
                {
                    tree.add(node.left);
                    nextLevelSum+=node.left.val;
                }
                if(node.right!=null)
                {
                    tree.add(node.right);
                    nextLevelSum+=node.right.val;
                }
            }
            for(TreeNode node : tmp)//å‡å»è‡ªå·±å’Œäº²å…„å¼Ÿçš„val
            {
                int childrenSum = (node.left != null ? node.left.val : 0) +
                                  (node.right != null ? node.right.val : 0);
                if (node.left != null) node.left.val = nextLevelSum - childrenSum;
                if (node.right != null) node.right.val = nextLevelSum - childrenSum;
            }
        }
        return root;
    }
}
```

