                if(node.left!=null)
                {
                    queue.offer(node.left);
                }
                if(node.right!=null)
                {
                    queue.offer(node.right);
                }
            }
            tree.add(one_level);//æŠŠè¿™å±‚ç»“ç‚¹åŠ å…¥æ ‘
        }
        return tree;
    }
