class Solution {
    public int removeDuplicates(int[] nums) {
        int n=nums.length;
        int left=0,right=2;//right-leftå§‹ç»ˆ>=2
        while(right<nums.length)
        {
            if(nums[left]!=nums[right])
            {
                nums[left+2]=nums[right];
                right++;
                left++;
            }
            else
            {
                n--;
                right++;
            }
        }
        return n;
    }
}
