class Solution {
    public int minDistance(String word1, String word2) {
        int s1=word1.length();
        int s2=word2.length();
        int[][] dp=new int[s1+1][s2+1];
        for(int i=1;i<=s1;i++)dp[i][0]=i;
        for(int i=1;i<=s2;i++)dp[0][i]=i;
        for(int i=1;i<=s1;i++)
