class Solution {
    public int getMaxLen(int[] nums) {
        int n=nums.length;
        int negative[]=new int[n];
        int postive[]=new int[n];
        if(nums[0]<0)negative[0]=1;
        if(nums[0]>0)postive[0]=1;
        for(int i=1;i<n;i++)
        {
            if(nums[i]>0)
            {
                postive[i]=postive[i-1]+1;
                if(negative[i-1]==0)negative[i]=0;
                if(negative[i-1]!=0)negative[i]=negative[i-1]+1;
                continue;
            }
            else if(nums[i]<0)
            {
                if(negative[i-1]==0)postive[i]=0;
                if(negative[i-1]>0)postive[i]=negative[i-1]+1;
                negative[i]=postive[i-1]+1;
                continue;
            }
            else
            {
                postive[i]=0;
                negative[i]=0;
            }
        }
        int maxx=0;
        for(int i=0;i<n;i++)
        {
            maxx=Math.max(maxx,postive[i]);
        }
        return maxx;
    }
}
```

