class Solution {
    public int f[][];
    public int val[];
    public int maxCoins(int[] nums) {
        int n=nums.length;
        val=new int[n+2];
        System.arraycopy(nums, 0, val, 1, n);
        val[0]=val[n+1]=1;
        f=new int [n+2][n+2];
        for(int bet=2;bet<n+2;bet++)//åŒºé—´é•¿åº¦ï¼Œä»2å¼€å§‹åˆ°n+1
        {
            for(int l=0;l+bet<n+2;l++)
            {
                int r=l+bet;
                for(int mid=l+1;mid<l+bet;mid++)
                {
                    int sum=val[l]*val[mid]*val[r];
                    sum+=f[l][mid]+f[mid][r];
                    f[l][r]=Math.max(f[l][r],sum);
                }
            }
        }
        return f[0][n+1];
    }
}
