class Solution {
public:
    int numDecodings(string s) {
        int n=s.size();
        if(s[0]=='0')return 0;
        s=" "+s;
        vector<int> dp(n+1,0);
        dp[0]=1;
        for(int i=1;i<=n;i++)
        {
            int a=s[i]-'0',b=s[i-1]-'0';
            if(a==0)
            {
                if(b==1 || b==2)dp[i]=dp[i-2];
                else return 0;
            }
            else
            {
                if(a>0 && a<10)dp[i]=dp[i-1];
                if(b*10+a>=10 && b*10+a<=26)dp[i]=dp[i-1]+dp[i-2];
            }
        }
        return dp[n];
    }
};

