int n, m,cnt;
double dis[2020];
bool vis[2020];
int head[2020],tot[2020];
struct edge {
	int to;
	int nxt;
	double w;
}Edge[2020];
void addedge(int u, int v, double w)
{
	Edge[++cnt].to = v;
	Edge[cnt].w = w;
	Edge[cnt].nxt = head[u];
	head[u] = cnt;
}
bool spfa(double mid);
int main()
{
	cin >> n >> m;
	while (m--)
	{
		int ai, bi, ci, di;
		cin >> ai >> bi >> ci >> di;
		addedge(bi, di, -log(ci*1.0/ai));//ç”±äºci/aiä¸€èˆ¬å°äº1ï¼Œå–å¯¹åä¸ºè´Ÿæ•°ï¼Œå› æ­¤å–å
	}
	double l = 0, r = 1.0;
	while (r - l >= 1e-8)
	{
		double mid = (l + r) / 2.0;
		if (spfa(-log(mid)))//midä¹Ÿå°äº1ï¼Œå–å
		{
			l = mid;
		}
		else
			r = mid;
	}
	printf("%.6lf", l);
	return 0;
}
bool spfa(double mid)
{
	queue<int> q;
	memset(dis, 0x3f, sizeof(dis));
	dis[1] = 0;
	memset(vis, 0, sizeof(vis));
	memset(tot, 0, sizeof(tot));
	for (int i = 1; i <= n; i++)//è´Ÿç¯ä¸ä¸€å®šæ˜¯åˆ°æºç‚¹çš„è·¯å¾„ä¸Šäº§ç”Ÿçš„ï¼ŒæŠŠæ‰€æœ‰ç‚¹å…¥é˜Ÿåˆ—
		q.push(i), vis[1] = 1;
	while (!q.empty())
	{
		int u = q.front();
		q.pop();
		vis[u] = false;
		for (int i = head[u]; i; i = Edge[i].nxt)
		{
			int to = Edge[i].to;
			if (dis[to] > dis[u] + Edge[i].w + mid)//åŸæœ¬æ˜¯ä¹˜æ³•ï¼Œå–å¯¹æ•°è½¬åŒ–ä¸ºåŠ æ³•ï¼ˆæ¯æ¬¡ä¹˜wä»£è¡¨æ¯ç»è¿‡ä¸€æ¡è¾¹éƒ½è¦åŠ wï¼‰
			{
				dis[to] = dis[u] + Edge[i].w + mid;
				tot[to] = tot[u] + 1;
				if (tot[to] >= n)return false;//è¯´æ˜æœ‰è´Ÿç¯
				if (!vis[to])
				{
					q.push(to);
					vis[to] = 1;
				}
			}
		}
	}
	return true;
}


