#include<bits/stdc++.h>
using namespace std;
const int N=100010;
int n,q,cnt,head[N*2],dep[N],fa[N][25];
struct edge{
	int to,next;
}e[N*2];
void add(int u,int v)
{
	e[++cnt].to=v;
	e[cnt].next=head[u];
	head[u]=cnt;
}
void dfs(int now,int father)
{
	dep[now]=dep[father]+1;
	fa[now][0]=father;
	for(int i=1;(1<<i)<=dep[now];i++)
	{
		fa[now][i]=fa[fa[now][i-1]][i-1];
	}
	for(int i=head[now];i;i=e[i].next)
	{
		int to=e[i].to;
		if(to!=father)
		{
			dfs(to,now);
		}
	}
}
int lca(int x,int y)
{
	if(dep[x]<dep[y])swap(x,y);
	for(int i=20;i>=0;i--)
	{
		if(dep[fa[x][i]]>=dep[y])x=fa[x][i];
		if(x==y)return x;
	}
	for(int i=20;i>=0;i--)
	{
		if(fa[x][i]!=fa[y][i])
		{
			x=fa[x][i];
			y=fa[y][i];
		}
	}
	return fa[x][0];
}
int dis(int a,int b)
{
	int c=lca(a,b);
	return abs(dep[c]-dep[a])+abs(dep[c]-dep[b]);
}
