#include<bits/stdc++.h>
using namespace std;
struct edge{
	int to,next;
}e[1010];
int head[1010],n,pop[1010],cnt;
int size[1010],ans=114514,f[1010];
void add(int u,int v)
{
	e[++cnt].to=v;
	e[cnt].next=head[u];
	head[u]=cnt;
}
void dfs(int x,int father,int dep)
{
	size[x]=pop[x];//å› ä¸ºæœ¬é¢˜ä¸­æ¯ä¸ªç‚¹æœ‰ç‚¹æƒï¼ˆäººå£ï¼‰ï¼Œå› æ­¤ç‚¹æƒè¦è®¡å…¥æ ‘çš„å¤§å°ã€‚
	for(int i=head[x];i;i=e[i].next)
	{
		if(e[i].to!=father)
		{
			dfs(e[i].to,x,dep+1);
			size[x]+=size[e[i].to];
		}
	}
	f[1]+=(pop[x]*dep);//f[1]ä»£è¡¨åˆ°f[1]çš„æ€»è·ç¦»ï¼Œä¹‹åä»f[1]å¼€å§‹å‘åè¿›è¡Œdp
}
void dp(int u,int father)
{
	for(int i=head[u];i;i=e[i].next)
	{
		if(e[i].to!=father)
		{
			f[e[i].to]=f[u]+size[1]-2*size[e[i].to];
			dp(e[i].to,u);
		}
		ans=min(f[u],ans);
	}
}
int main()
{
	cin>>n;
	for(int i=1;i<=n;i++)
	{
		cin>>pop[i];
		int u,v;
		cin>>u>>v;
		if(u)add(i,u);
		if(v)add(i,v);
	}
	dfs(1,0,0);
	dp(1,0);
	cout<<ans;
	return 0;
}
```

